from pybricks.parameters import Button, Direction, Port
from pybricks.pupdevices import DCMotor, Remote
from pybricks.tools import wait

# Set up all devices.
pumotor1 = DCMotor(Port.A, Direction.CLOCKWISE)
pumotor2 = DCMotor(Port.B, Direction.COUNTERCLOCKWISE)
remote = Remote(timeout=None)

# Initialize variables.
masterSpeed = 0
newSpeed = 0

def increaseSpeed():
    global newSpeed, masterSpeed
    newSpeed = 0
    if -100 < masterSpeed < 100:
        newSpeed = masterSpeed + 10
    elif masterSpeed == -100:
        newSpeed = masterSpeed + 10
    else:
        newSpeed = masterSpeed
    pumotor1.dc(newSpeed)
    pumotor2.dc(newSpeed)
    masterSpeed = newSpeed
    wait(500)

def decreaseSpeed():
    global newSpeed, masterSpeed
    newSpeed = 0
    if -100 < masterSpeed < 100:
        newSpeed = masterSpeed - 10
    elif masterSpeed == 100:
        newSpeed = masterSpeed - 10
    else:
        newSpeed = masterSpeed
    pumotor1.dc(newSpeed)
    pumotor2.dc(newSpeed)
    masterSpeed = newSpeed
    wait(500)


# The main program starts here.
while True:
    if Button.LEFT_PLUS in remote.buttons.pressed():
        increaseSpeed()
    elif Button.LEFT in remote.buttons.pressed():
        pumotor1.stop()
        pumotor2.stop()
    elif Button.LEFT_MINUS in remote.buttons.pressed():
        decreaseSpeed()
    elif Button.RIGHT_PLUS in remote.buttons.pressed():
        increaseSpeed()
    elif Button.RIGHT_MINUS in remote.buttons.pressed():
        decreaseSpeed()
    elif Button.RIGHT in remote.buttons.pressed():
        pumotor1.stop()
        pumotor2.stop()
    else:
        pass
